// import logo from './logo.svg';
import {MenuItem, FormControl, Select, Card, CardContent} from "@mui/material";
import { useEffect, useState } from "react";
import InfoBox from './InfoBox';
import Map from './Map';
import './App.css';

function App() {
  const [countries, setCountries] = useState([]);
  const [country, setCountry] = useState("Worldwide");
  const [countryInfo, setCountryInfo] = useState({});

  // https://disease.sh/v3/covid-19/countries

  useEffect(()=>{
    const getCountriesData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
      .then((res)=>res.json())
      .then((data)=>{
        const countries = data.map((country)=>({
            name: country.country, 
            value: country.countryInfo.iso2,
          }));

          setCountries(countries);
      })
    };


    getCountriesData();
  }, []);

  const onCountryChange = async (event) => {
    const countryCode = event.target.value;
    setCountry(countryCode);

    const url = countryCode === "Worldwide" 
    ? 'https://disease.sh/v3/covid-19/all' : 
    `https://disease.sh/v3/covid-19/countries/${countryCode}`;

    await fetch(url)
      .then(response => response.json())
      .then(data => {
        setCountryInfo(data);
    })

    // https://disease.sh/v3/covid-19/all
    // https://disease.sh/v3/covid-19/countries    
  }
  console.log(countryInfo);




  return (
    <div className="app">
      <div className="app_left">
        <div className="app_header"> 
          <h1>COVID-19 TRACKER</h1>
          <FormControl className="app_dropdown">
            <Select variant="outlined" onChange={onCountryChange} value={country}>
              <MenuItem value="Worldwide"> Worldwide </MenuItem>
              {countries.map((country) => {
                return <MenuItem value={country.value}> {country.name} </MenuItem>
              })}
            </Select>
          </FormControl>   
        </div>      

        <div className="app_stats">
            <InfoBox title="Coronavirus Cases" cases={countryInfo.todayCases} total={2000} />

            <InfoBox title="Recovered" cases={countryInfo.todayRecovered} total={3000} />

            <InfoBox title="Deaths" cases={69} total={123} />
        </div> 

        <Map />

      </div>

      <Card className="app_right">
        <CardContent>
          <h3>Live cases by country.</h3>
          <h3>Worldwide new cases.</h3>
        </CardContent>
      </Card>

    </div>
  );
}

export default App;
